import React, { useState, useRef, useEffect, useCallback } from "react";
import questionBank from "../data/questions-corrected";
import sampleAnswers from "../data/sampleAnswers";
import './SpeechEvaluator.css';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, BarElement, CategoryScale, LinearScale, Title, Tooltip, Legend } from 'chart.js';
import jsPDF from 'jspdf';

ChartJS.register(BarElement, CategoryScale, LinearScale, Title, Tooltip, Legend);

const getBandColor = (band) => {
  if (band >= 8.5) return "#2e7d32";
  if (band >= 7) return "#388e3c";
  if (band >= 6.5) return "#689f38";
  if (band >= 6) return "#afb42b";
  if (band >= 5.5) return "#f57f17";
  if (band >= 5) return "#ff8f00";
  if (band >= 4) return "#d32f2f";
  return "#d32f2f";
};

const createIELTSTest = () => {
  const availableTopics = [...questionBank.part1Topics];
  const shuffled = availableTopics.sort(() => 0.5 - Math.random());
  const introTopic = shuffled[0];
  const introQuestions = [...introTopic.questions].sort(() => 0.5 - Math.random()).slice(0, 2);
  const secondTopic = shuffled[1];
  const secondQuestions = [...secondTopic.questions].sort(() => 0.5 - Math.random()).slice(0, Math.random() > 0.5 ? 2 : 3);
  let part1 = [];
  introQuestions.forEach(q => part1.push({ ...q, topicName: introTopic.topicName, part: 1, expectedDuration: 30 }));
  secondQuestions.forEach(q => part1.push({ ...q, topicName: secondTopic.topicName, part: 1, expectedDuration: 30 }));
  const cueCards = [...questionBank.part2CueCards];
  const selectedCard = cueCards[Math.floor(Math.random() * cueCards.length)];
  const part2 = { ...selectedCard, part: 2, expectedDuration: 120, preparationTime: 60 };
  const relatedDiscussion = questionBank.part3Discussions.find(d => d.relatedToPart2 === selectedCard.id);
  let part3 = [];
  if (relatedDiscussion) {
    const discQuestions = [...relatedDiscussion.questions].sort(() => 0.5 - Math.random());
    const count = Math.min(discQuestions.length, Math.floor(Math.random() * 3) + 4);
    part3 = discQuestions.slice(0, count).map(q => ({ ...q, part: 3, topicName: relatedDiscussion.topicTheme, expectedDuration: 60 }));
  }
  const allQuestions = [...part1, { ...part2, prompt: part2.cueCard.mainPrompt }, ...part3];
  return {
    testId: `ielts_${Date.now()}`,
    title: `IELTS Speaking Test - ${new Date().toLocaleDateString()}`,
    testStructure: {
      part1: { name: "Part 1: Introduction & Interview", duration: "4-5 minutes", questions: part1, topics: [introTopic.topicName, secondTopic.topicName] },
      part2: { name: "Part 2: Individual Long Turn", duration: "3-4 minutes", question: part2, cueCard: part2.cueCard },
      part3: { name: "Part 3: Discussion", duration: "4-5 minutes", questions: part3, theme: relatedDiscussion ? relatedDiscussion.topicTheme : "General" },
    },
    allQuestions,
    totalQuestions: allQuestions.length,
  };
};

class SimplePronunciationAnalyzer {
  constructor() {
    this.audioContext = null;
    this.analyser = null;
    this.features = { pitch: [], rms: [], spectralCentroid: [], zcr: [] };
    this.isRunning = false;
  }
  async initialize() {
    try {
      this.audioContext = new (window.AudioContext || window.webkitAudioContext)();
      if (this.audioContext.state === 'suspended') await this.audioContext.resume();
      return true;
    } catch {
      return false;
    }
  }
  async startAnalysis(stream) {
    if (!this.audioContext) throw new Error("No audioContext");
    const source = this.audioContext.createMediaStreamSource(stream);
    this.analyser = this.audioContext.createAnalyser();
    this.analyser.fftSize = 2048;
    this.analyser.smoothingTimeConstant = 0.8;
    source.connect(this.analyser);
    this.isRunning = true;
    this.extractFeatures();
  }
  extractFeatures() {
    if (!this.isRunning) return;
    const freqData = new Float32Array(this.analyser.frequencyBinCount);
    const timeData = new Float32Array(this.analyser.fftSize);
    this.analyser.getFloatFrequencyData(freqData);
    this.analyser.getFloatTimeDomainData(timeData);
    const ts = this.audioContext.currentTime;
    const rms = this.calcRMS(timeData);
    const zcr = this.calcZCR(timeData);
    const centroid = this.calcSpectralCentroid(freqData);
    const pitch = this.calcPitch(timeData);
    this.features.rms.push({ value: rms, ts });
    this.features.zcr.push({ value: zcr, ts });
    this.features.spectralCentroid.push({ value: centroid, ts });
    if (pitch > 50 && pitch < 800) this.features.pitch.push({ value: pitch, ts });
    requestAnimationFrame(() => this.extractFeatures());
  }
  calcRMS(data) {
    let sum = 0;
    for (let i = 0; i < data.length; i++) sum += data[i] * data[i];
    return Math.sqrt(sum / data.length);
  }
  calcZCR(data) {
    let count = 0;
    for (let i = 1; i < data.length; i++) if ((data[i] >= 0) !== (data[i - 1] >= 0)) count++;
    return count / (data.length - 1);
  }
  calcSpectralCentroid(freqData) {
    let num = 0, den = 0;
    for (let i = 0; i < freqData.length; i++) {
      const mag = Math.pow(10, freqData[i] / 10);
      const freq = (i * this.audioContext.sampleRate) / (2 * freqData.length);
      num += freq * mag;
      den += mag;
    }
    return den ? num / den : 0;
  }
  calcPitch(data) {
    const sr = this.audioContext.sampleRate;
    const minPeriod = Math.floor(sr / 800);
    const maxPeriod = Math.floor(sr / 50);
    let bestCorr = 0, bestPeriod = -1;
    for (let per = minPeriod; per < maxPeriod && per < data.length / 2; per++) {
      let corr = 0;
      for (let i = 0; i < data.length - per; i++) corr += data[i] * data[i + per];
      if (corr > bestCorr) { bestCorr = corr; bestPeriod = per; }
    }
    return bestPeriod > 0 ? sr / bestPeriod : 0;
  }
  stop() { this.isRunning = false; }
  getFeatures() { return { ...this.features }; }
  reset() { this.features = { pitch: [], rms: [], spectralCentroid: [], zcr: [] }; this.isRunning = false; }
}

export default function SpeechEvaluator() {
  // States
  const [currentTest, setCurrentTest] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [currentPart, setCurrentPart] = useState(1);
  const [answers, setAnswers] = useState([]);
  const [recording, setRecording] = useState(false);
  const [loading, setLoading] = useState(false);
  const [feedbacks, setFeedbacks] = useState(null);
  const [testSummary, setTestSummary] = useState(null);
  const [error, setError] = useState(null);
  const [recordingTime, setRecordingTime] = useState(0);
  const [showResults, setShowResults] = useState(false);
  const [preparationTime, setPreparationTime] = useState(0);
  const [isPreparing, setIsPreparing] = useState(false);
  const [autoStart, setAutoStart] = useState(false);
  const [initializationError, setInitializationError] = useState(null);
  const [isReady, setIsReady] = useState(false);
  const [audioFeatures, setAudioFeatures] = useState({});
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const pronunciationAnalyzerRef = useRef(null);
  const recognitionRef = useRef(null);
  const recordingTimerRef = useRef(null);
  const preparationTimerRef = useRef(null);
  const currentStreamRef = useRef(null);

  // Initialize
  useEffect(() => {
    async function init() {
      try {
        const test = createIELTSTest();
        setCurrentTest(test);
        setAnswers(Array(test.totalQuestions).fill(""));
        pronunciationAnalyzerRef.current = new SimplePronunciationAnalyzer();
        setIsReady(true);
      } catch {
        setInitializationError("Failed to initialize. Refresh page.");
        setIsReady(true);
      }
    }
    init();
    return () => {
      if (recordingTimerRef.current) clearInterval(recordingTimerRef.current);
      if (preparationTimerRef.current) clearInterval(preparationTimerRef.current);
      if (currentStreamRef.current) {
        currentStreamRef.current.getTracks().forEach(track => track.stop());
      }
      if (pronunciationAnalyzerRef.current) {
        pronunciationAnalyzerRef.current.stop();
      }
    };
  }, []);

  // Auto-start recording after preparation
  useEffect(() => {
    if (autoStart && !isPreparing && preparationTime === 0) {
      const to = setTimeout(() => {
        toggleRecording();
        setAutoStart(false);
      }, 500);
      return () => clearTimeout(to);
    }
  }, [autoStart, preparationTime, isPreparing]);

  // Timer handlers
  const startRecordingTimer = useCallback(() => {
    setRecordingTime(0);
    recordingTimerRef.current = setInterval(() => setRecordingTime(t => t + 1), 1000);
  }, []);
  const stopRecordingTimer = useCallback(() => {
    if (recordingTimerRef.current) {
      clearInterval(recordingTimerRef.current);
      recordingTimerRef.current = null;
    }
  }, []);
  const startPreparationTimer = useCallback(() => {
    setPreparationTime(60);
    setAutoStart(true);
    setIsPreparing(true);
    preparationTimerRef.current = setInterval(() => {
      setPreparationTime(t => {
        if (t <= 1) {
          clearInterval(preparationTimerRef.current);
          setIsPreparing(false);
          return 0;
        }
        return t - 1;
      });
    }, 1000);
  }, []);

  const formatTime = (seconds) => {
    const m = Math.floor(seconds / 60);
    const s = seconds % 60;
    return `${m}:${s.toString().padStart(2, "0")}`;
  };

  // Navigation helpers
  const getCurrentPartInfo = () => {
    if (!currentTest) return null;
    const len = currentTest.testStructure.part1.questions.length;
    if (currentQuestionIndex < len) return { part: 1, info: currentTest.testStructure.part1 };
    if (currentQuestionIndex === len) return { part: 2, info: currentTest.testStructure.part2 };
    return { part: 3, info: currentTest.testStructure.part3 };
  };

  const toggleRecording = async () => {
    if (recording) {
      stopRecording();
    } else {
      await startRecording();
    }
  };

  async function startRecording() {
    if (!("webkitSpeechRecognition" in window) && !("SpeechRecognition" in window)) {
      setError("Speech recognition not supported in this browser.");
      return;
    }
    if (pronunciationAnalyzerRef.current && !pronunciationAnalyzerRef.current.audioContext) {
      await pronunciationAnalyzerRef.current.initialize();
    }
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: { echoCancellation: true, noiseSuppression: true, autoGainControl: false } });
      currentStreamRef.current = stream;
      if (pronunciationAnalyzerRef.current) {
        pronunciationAnalyzerRef.current.reset();
        await pronunciationAnalyzerRef.current.startAnalysis(stream);
        setIsAnalyzing(true);
      }
      const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
      const recognition = new SpeechRecognition();
      recognition.lang = "en-US";
      recognition.interimResults = false;
      recognition.continuous = true;
      recognition.onstart = () => { setRecording(true); setError(null); startRecordingTimer(); };
      recognition.onresult = e => {
        let text = Array.from(e.results).map(r => r[0].transcript).join(" ");
        setAnswers(a => { const copy = [...a]; copy[currentQuestionIndex] = text.trim(); return copy; });
      };
      recognition.onend = () => {
        setRecording(false);
        stopRecordingTimer();
      };
      recognition.onerror = e => {
        setError(`Speech recognition error: ${e.error}`);
        setRecording(false);
        stopRecordingTimer();
        setIsAnalyzing(false);
      };
      recognitionRef.current = recognition;
      recognition.start();
    } catch (err) {
      setError("Error accessing microphone.");
    }
  }

  function stopRecording() {
    if (recognitionRef.current) recognitionRef.current.stop();
    if (pronunciationAnalyzerRef.current && isAnalyzing) {
      pronunciationAnalyzerRef.current.stop();
      let feats = pronunciationAnalyzerRef.current.getFeatures();
      setAudioFeatures(f => ({ ...f, [currentQuestionIndex]: feats }));
      setIsAnalyzing(false);
    }
    if (currentStreamRef.current) {
      currentStreamRef.current.getTracks().forEach(t => t.stop());
    }
    setTimeout(() => {
      if (currentQuestionIndex < currentTest.totalQuestions - 1) {
        setCurrentQuestionIndex(currentQuestionIndex + 1);
      }
    }, 1500);
  }

  const goPrev = () => {
    if (currentQuestionIndex > 0) setCurrentQuestionIndex(currentQuestionIndex - 1);
  };

  async function submitTest() {
    setLoading(true);
    setError(null);
    try {
      const res = await fetch("/api/analyze-batch", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          testId: currentTest.testId,
          questions: currentTest.allQuestions.map(q => q.prompt),
          answers,
          audioFeatures,
          testType: "IELTS_AUTHENTIC"
        }),
      });
      if (!res.ok) throw new Error(`Error ${res.status}`);
      const data = await res.json();
      setFeedbacks(data.feedbacks);
      setTestSummary(data.testSummary);
      setShowResults(true);
    } catch (e) {
      setError("Submission error, please try again.");
    } finally {
      setLoading(false);
    }
  }

  function resetTest() {
    const test = createIELTSTest();
    setCurrentTest(test);
    setAnswers(Array(test.totalQuestions).fill(''));
    setCurrentQuestionIndex(0);
    setCurrentPart(1);
    setFeedbacks(null);
    setTestSummary(null);
    setShowResults(false);
    setError(null);
    setRecordingTime(0);
    setPreparationTime(0);
    setIsPreparing(false);
    setAutoStart(false);
    if (pronunciationAnalyzerRef.current) pronunciationAnalyzerRef.current.reset();
  }

  if (!isReady) return (<div className="loading-container">{initializationError || "Initializing..."}</div>);
  if (!currentTest) return null;

  const partInfo = getCurrentPartInfo();

  const bandData = {
    labels: ['Part 1', 'Part 2', 'Part 3'],
    datasets: [{
      label: 'Bands',
      data: [
        testSummary?.part1Band || 0,
        testSummary?.part2Band || 0,
        testSummary?.part3Band || 0
      ],
      backgroundColor: [
        getBandColor(testSummary?.part1Band),
        getBandColor(testSummary?.part2Band),
        getBandColor(testSummary?.part3Band)
      ]
    }]
  };

  return (
    <div className="speech-evaluator">
      {!showResults ? (
        <>
          <header className="test-header">
            <h1>{currentTest.title}</h1>
            <div className="test-info">
              <div className="test-id">ID: {currentTest.testId}</div>
              <div className="progress">Progress: {answers.filter(a => a.trim()).length} / {currentTest.totalQuestions}</div>
            </div>
          </header>

          <section className="test-content">
            <article className="part-indicator">
              <h2>{partInfo.info.name}</h2>
              <p className="part-duration">{partInfo.info.duration}</p>
              {partInfo.part === 1 && <p>Topics: {partInfo.info.topics.join(", ")}</p>}
              {partInfo.part === 2 && <p>Preparation: 1 min  Auto record  Speak 1-2 min</p>}
              {partInfo.part === 3 && <p>Theme: {partInfo.info.theme}</p>}
            </article>

            <article className="question-section">
              <div className="question-header">
                <span className="question-number">Q{currentQuestionIndex + 1}</span>
                {partInfo.part === 1 && (
                  <span className="topic-badge">{currentTest.allQuestions[currentQuestionIndex].topicName}</span>
                )}
              </div>
              <h3 className="question-prompt">{currentTest.allQuestions[currentQuestionIndex].prompt}</h3>
              <div className="duration-guide">Approx. {currentTest.allQuestions[currentQuestionIndex].expectedDuration || 30} seconds</div>
              <div className="answer-section">
                <label>Your Answer</label>
                <textarea
                  value={answers[currentQuestionIndex]}
                  onChange={e => {
                    const val = e.target.value;
                    setAnswers(a => {
                      const copy = [...a];
                      copy[currentQuestionIndex] = val;
                      return copy;
                    });
                  }}
                  className="form-control"
                  rows={5}
                />
              </div>
            </article>

            <article className="recording-section">
              <button
                onClick={toggleRecording}
                className={`btn ${recording ? 'btn--danger' : 'btn--primary'}`}
              >
                {recording ? "Stop Recording" : "Start Recording"}
              </button>
              <div>Recording Time: {formatTime(recordingTime)}</div>
            </article>

            <article className="navigation-section">
              <div className="nav-buttons">
                <button onClick={goPrev} disabled={currentQuestionIndex === 0} className="btn btn--secondary">Previous</button>
                <button onClick={() => {
                  if (currentQuestionIndex < currentTest.totalQuestions - 1) setCurrentQuestionIndex(idx => idx + 1);
                }} disabled={currentQuestionIndex >= currentTest.totalQuestions -1} className="btn btn--secondary">Next</button>
                <button onClick={submitTest} disabled={loading || answers.filter(a => a.trim()).length < currentTest.totalQuestions} className="btn btn--success">
                  {loading ? "Submitting..." : "Submit"}
                </button>
              </div>
              {error && <div className="error-message">{error}</div>}
            </article>
          </section>
        </>
      ) : (
        <section className="results-container">
          <h2>Results</h2>
          <section className="test-summary">
            <div className="band-score" style={{ borderLeftColor: getBandColor(testSummary.part1Band) }}>
              <span>Part 1</span>
              <span>{testSummary.part1Band}</span>
            </div>
            <div className="band-score" style={{ borderLeftColor: getBandColor(testSummary.part2Band) }}>
              <span>Part 2</span>
              <span>{testSummary.part2Band}</span>
            </div>
            <div className="band-score" style={{ borderLeftColor: getBandColor(testSummary.part3Band) }}>
              <span>Part 3</span>
              <span>{testSummary.part3Band}</span>
            </div>
            <div className="band-score overall" style={{ background: `linear-gradient(135deg, ${getBandColor(testSummary.overallBand)}, #0d6f62)` }}>
              <span>Overall</span>
              <span>{testSummary.overallBand}</span>
            </div>
          </section>

          <section className="band-chart-wrapper" style={{ maxWidth: 450, margin: "0 auto" }}>
            <Bar data={bandData} options={{
              scales: { y: { min: 0, max: 9, ticks: { stepSize: 0.5 } } },
              plugins: { legend: { display: false } }
            }} />
          </section>

          <button
            className="btn btn--primary export-pdf-btn"
            onClick={() => {
              if (!testSummary) return;
              const doc = new jsPDF();
              doc.setFontSize(18);
              doc.text("IELTS Speaking Feedback", 10, 10);
              doc.setFontSize(14);
              doc.text(`Part 1 Band: ${testSummary.part1Band}`, 10, 30);
              doc.text(`Part 2 Band: ${testSummary.part2Band}`, 10, 40);
              doc.text(`Part 3 Band: ${testSummary.part3Band}`, 10, 50);
              doc.text(`Overall Band: ${testSummary.overallBand}`, 10, 60);
              if (feedbacks) {
                let y = 80;
                feedbacks.forEach((fb, idx) => {
                  doc.text(`Q${idx + 1}: ${fb.question}`, 10, y);
                  y += 8;
                  doc.text(`Your Answer: ${fb.answer}`, 10, y);
                  y += 8;
                  doc.text(`Feedback: ${fb.feedback}`, 10, y);
                  y += 8;
                  const sample = sampleAnswers.find(s => s.question === fb.question)?.sample;
                  if (sample) {
                    doc.text(`Sample Answer: ${sample}`, 10, y);
                    y += 12;
                  } else {
                    y += 4;
                  }
                  if (y > 270) {
                    doc.addPage();
                    y = 20;
                  }
                });
              }
              doc.save("IELTS_Feedback.pdf");
            }}
          >
            Export PDF
          </button>

          <section className="detailed-feedback">
            {feedbacks && feedbacks.map((fb, idx) => (
              <article key={idx} className="feedback-section">
                <h3>Q{idx + 1}: {fb.question}</h3>
                <p><strong>Your Answer:</strong> {fb.answer}</p>
                <p><strong>Feedback:</strong> {fb.feedback}</p>
                <p><strong>Sample Answer:</strong> {sampleAnswers.find(s => s.question === fb.question)?.sample || "Coming soon..."}</p>
              </article>
            ))}
          </section>

          <button className="btn btn--secondary" onClick={() => {
            setShowResults(false);
            setFeedbacks(null);
            setTestSummary(null);
            setAnswers(Array(currentTest.totalQuestions).fill(''));
            setCurrentQuestionIndex(0);
            setCurrentPart(1);
          }}>Retake Test</button>
        </section>
      )}
    </div>
  );
}
