// Enhanced SpeechEvaluator.jsx - Complete IELTS Speech Evaluator with Question Bank Integration
import React, { useState, useRef, useEffect, useCallback } from "react";
import questionBank from "../data/questions";
import './SpeechEvaluator.css';

// Function to create randomized tests from question bank
const createRandomTest = () => {
  // Filter questions by difficulty for balanced test
  const easyQuestions = questionBank.filter(q => q.difficulty === "easy");
  const mediumQuestions = questionBank.filter(q => q.difficulty === "medium");
  const hardQuestions = questionBank.filter(q => q.difficulty === "hard");

  // Create a balanced test with 1 easy, 1 medium, 1 hard question
  const selectedQuestions = [
    easyQuestions[Math.floor(Math.random() * easyQuestions.length)],
    mediumQuestions[Math.floor(Math.random() * mediumQuestions.length)],
    hardQuestions[Math.floor(Math.random() * hardQuestions.length)]
  ];

  return {
    testId: `test_${Date.now()}`,
    title: `IELTS Speaking Practice - ${new Date().toLocaleDateString()}`,
    questions: selectedQuestions.map(q => q.prompt),
    sampleAnswers: selectedQuestions.map(q => q.sampleAnswer),
    categories: selectedQuestions.map(q => q.category),
    difficulties: selectedQuestions.map(q => q.difficulty),
    questionIds: selectedQuestions.map(q => q.id)
  };
};

// Alternative function to create tests by specific criteria
const createCustomTest = (criteria = {}) => {
  let filteredQuestions = questionBank;

  // Filter by category if specified
  if (criteria.category) {
    filteredQuestions = filteredQuestions.filter(q => 
      q.category.toLowerCase().includes(criteria.category.toLowerCase())
    );
  }

  // Filter by difficulty if specified
  if (criteria.difficulty) {
    filteredQuestions = filteredQuestions.filter(q => q.difficulty === criteria.difficulty);
  }

  // Shuffle and take specified number of questions (default 3)
  const numQuestions = criteria.numQuestions || 3;
  const shuffled = [...filteredQuestions].sort(() => Math.random() - 0.5);
  const selectedQuestions = shuffled.slice(0, numQuestions);

  return {
    testId: `custom_test_${Date.now()}`,
    title: criteria.title || `Custom IELTS Test - ${criteria.category || 'Mixed Topics'}`,
    questions: selectedQuestions.map(q => q.prompt),
    sampleAnswers: selectedQuestions.map(q => q.sampleAnswer),
    categories: selectedQuestions.map(q => q.category),
    difficulties: selectedQuestions.map(q => q.difficulty),
    questionIds: selectedQuestions.map(q => q.id)
  };
};

// Pronunciation Analyzer Class (simplified for React integration)
class SimplePronunciationAnalyzer {
  constructor() {
    this.audioContext = null;
    this.analyser = null;
    this.features = {
      pitch: [],
      rms: [],
      spectralCentroid: [],
      zcr: []
    };
    this.isAnalyzing = false;
  }

  async initialize() {
    try {
      this.audioContext = new (window.AudioContext || window.webkitAudioContext)();
      return true;
    } catch (error) {
      console.error('Failed to initialize audio context:', error);
      return false;
    }
  }

  async startAnalysis(stream) {
    if (!this.audioContext) {
      throw new Error('Audio context not initialized');
    }

    const source = this.audioContext.createMediaStreamSource(stream);
    this.analyser = this.audioContext.createAnalyser();

    this.analyser.fftSize = 2048;
    this.analyser.smoothingTimeConstant = 0.8;
    this.analyser.minDecibels = -90;
    this.analyser.maxDecibels = -10;

    source.connect(this.analyser);
    this.isAnalyzing = true;
    this.startFeatureExtraction();
  }

  startFeatureExtraction() {
    const bufferLength = this.analyser.frequencyBinCount;
    const dataArray = new Float32Array(bufferLength);
    const timeDataArray = new Float32Array(this.analyser.fftSize);

    const extractFeatures = () => {
      if (!this.isAnalyzing) return;

      this.analyser.getFloatFrequencyData(dataArray);
      this.analyser.getFloatTimeDomainData(timeDataArray);

      const timestamp = this.audioContext.currentTime;
      const rms = this.calculateRMS(timeDataArray);
      const zcr = this.calculateZCR(timeDataArray);
      const spectralCentroid = this.calculateSpectralCentroid(dataArray);
      const pitch = this.detectPitch(timeDataArray);

      this.features.rms.push({ value: rms, timestamp });
      this.features.zcr.push({ value: zcr, timestamp });
      this.features.spectralCentroid.push({ value: spectralCentroid, timestamp });

      if (pitch && pitch > 50 && pitch < 800) {
        this.features.pitch.push({ frequency: pitch, timestamp });
      }

      requestAnimationFrame(extractFeatures);
    };

    extractFeatures();
  }

  calculateRMS(timeData) {
    let sum = 0;
    for (let i = 0; i < timeData.length; i++) {
      sum += timeData[i] * timeData[i];
    }
    return Math.sqrt(sum / timeData.length);
  }

  calculateZCR(timeData) {
    let zeroCrossings = 0;
    for (let i = 1; i < timeData.length; i++) {
      if ((timeData[i] >= 0) !== (timeData[i - 1] >= 0)) {
        zeroCrossings++;
      }
    }
    return zeroCrossings / (timeData.length - 1);
  }

  calculateSpectralCentroid(freqData) {
    let weightedSum = 0;
    let magnitudeSum = 0;

    for (let i = 0; i < freqData.length; i++) {
      const magnitude = Math.pow(10, freqData[i] / 10);
      const frequency = (i * this.audioContext.sampleRate) / (2 * freqData.length);

      weightedSum += frequency * magnitude;
      magnitudeSum += magnitude;
    }

    return magnitudeSum > 0 ? weightedSum / magnitudeSum : 0;
  }

  detectPitch(timeData) {
    const sampleRate = this.audioContext.sampleRate;
    const minPeriod = Math.floor(sampleRate / 800);
    const maxPeriod = Math.floor(sampleRate / 50);

    let bestCorrelation = 0;
    let bestPeriod = 0;

    for (let period = minPeriod; period < Math.min(maxPeriod, timeData.length / 2); period++) {
      let correlation = 0;
      for (let i = 0; i < timeData.length - period; i++) {
        correlation += timeData[i] * timeData[i + period];
      }

      if (correlation > bestCorrelation) {
        bestCorrelation = correlation;
        bestPeriod = period;
      }
    }

    return bestPeriod > 0 ? sampleRate / bestPeriod : 0;
  }

  analyzeStressPatterns() {
    if (this.features.rms.length < 10) {
      return {
        stressPattern: '',
        rhythmScore: 0.5,
        intonationVariation: 0.5,
        stressAccuracy: 0.5
      };
    }

    const syllables = this.detectSyllables();
    let stressPattern = '';
    let stressedCount = 0;

    syllables.forEach(syllable => {
      const stressScore = this.calculateSyllableStress(syllable);
      if (stressScore > 0.6) {
        stressPattern += 'S';
        stressedCount++;
      } else {
        stressPattern += 'U';
      }
    });

    const stressRatio = syllables.length > 0 ? stressedCount / syllables.length : 0;
    const stressAccuracy = stressRatio >= 0.2 && stressRatio <= 0.4 ? 
      Math.min(stressRatio / 0.3, 1.0) : 0.5;

    return {
      stressPattern,
      rhythmScore: this.calculateRhythm(syllables),
      intonationVariation: this.calculateIntonation(),
      stressAccuracy
    };
  }

  detectSyllables() {
    const threshold = this.calculateEnergyThreshold();
    const syllables = [];
    let currentSyllable = null;
    let inSyllable = false;

    this.features.rms.forEach((rmsData, index) => {
      const energy = rmsData.value;
      const isVoiced = energy > threshold;

      if (isVoiced && !inSyllable) {
        currentSyllable = {
          startTime: rmsData.timestamp,
          peakEnergy: energy,
          energySum: energy,
          samples: 1
        };
        inSyllable = true;
      } else if (isVoiced && inSyllable) {
        currentSyllable.peakEnergy = Math.max(currentSyllable.peakEnergy, energy);
        currentSyllable.energySum += energy;
        currentSyllable.samples++;
      } else if (!isVoiced && inSyllable) {
        currentSyllable.endTime = rmsData.timestamp;
        currentSyllable.duration = currentSyllable.endTime - currentSyllable.startTime;
        currentSyllable.avgEnergy = currentSyllable.energySum / currentSyllable.samples;
        syllables.push(currentSyllable);
        inSyllable = false;
      }
    });

    return syllables;
  }

  calculateSyllableStress(syllable) {
    const avgIntensity = this.features.rms.reduce((sum, r) => sum + r.value, 0) / this.features.rms.length;
    const intensityScore = Math.min(syllable.avgEnergy / Math.max(avgIntensity, 0.001), 2.0);
    return intensityScore / 2.0;
  }

  calculateRhythm(syllables) {
    if (syllables.length < 3) return 0.5;
    const intervals = [];
    for (let i = 1; i < syllables.length; i++) {
      intervals.push(syllables[i].startTime - syllables[i-1].startTime);
    }
    const avgInterval = intervals.reduce((sum, interval) => sum + interval, 0) / intervals.length;
    const variance = intervals.reduce((sum, interval) => sum + Math.pow(interval - avgInterval, 2), 0) / intervals.length;
    const coefficient = Math.sqrt(variance) / Math.max(avgInterval, 0.1);
    return Math.max(0, 1 - coefficient);
  }

  calculateIntonation() {
    if (this.features.pitch.length < 10) return 0.5;
    const pitches = this.features.pitch.map(p => p.frequency);
    const minPitch = Math.min(...pitches);
    const maxPitch = Math.max(...pitches);
    const avgPitch = pitches.reduce((sum, p) => sum + p, 0) / pitches.length;
    const pitchRange = (maxPitch - minPitch) / avgPitch;
    return Math.min(pitchRange / 0.5, 1.0);
  }

  calculateEnergyThreshold() {
    if (this.features.rms.length === 0) return 0.01;
    const energies = this.features.rms.map(r => r.value).sort((a, b) => a - b);
    return energies[Math.floor(energies.length * 0.75)] * 0.25;
  }

  stopAnalysis() {
    this.isAnalyzing = false;
  }

  getFeatures() {
    return {
      ...this.features,
      analysis: this.analyzeStressPatterns()
    };
  }

  reset() {
    this.features = { pitch: [], rms: [], spectralCentroid: [], zcr: [] };
    this.isAnalyzing = false;
  }
}

// Main Speech Evaluator Component
function SpeechEvaluator() {
  // State management
  const [currentTest, setCurrentTest] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [recording, setRecording] = useState(false);
  const [loading, setLoading] = useState(false);
  const [feedbacks, setFeedbacks] = useState(null);
  const [testSummary, setTestSummary] = useState(null);
  const [error, setError] = useState(null);
  const [recordingTime, setRecordingTime] = useState(0);
  const [showResults, setShowResults] = useState(false);
  const [testMode, setTestMode] = useState('random'); // 'random', 'category', 'difficulty'
  const [customCriteria, setCustomCriteria] = useState({});

  // Pronunciation analysis state
  const [pronunciationAnalyzer, setPronunciationAnalyzer] = useState(null);
  const [audioFeatures, setAudioFeatures] = useState({});
  const [isAnalyzingPronunciation, setIsAnalyzingPronunciation] = useState(false);
  const [realTimeFeatures, setRealTimeFeatures] = useState({
    pitch: 0,
    intensity: 0,
    stressPattern: ''
  });

  // Refs
  const recognitionRef = useRef(null);
  const recordingTimerRef = useRef(null);
  const currentStreamRef = useRef(null);

  // Initialize test and pronunciation analyzer
  useEffect(() => {
    const test = createRandomTest();
    setCurrentTest(test);
    setAnswers(Array(test.questions.length).fill(""));

    // Initialize pronunciation analyzer
    const initAnalyzer = async () => {
      try {
        const analyzer = new SimplePronunciationAnalyzer();
        const initialized = await analyzer.initialize();
        if (initialized) {
          setPronunciationAnalyzer(analyzer);
        }
      } catch (error) {
        console.error('Failed to initialize pronunciation analyzer:', error);
      }
    };

    initAnalyzer();
  }, []);

  // Real-time feature updates
  useEffect(() => {
    let interval;
    if (isAnalyzingPronunciation && pronunciationAnalyzer) {
      interval = setInterval(() => {
        const features = pronunciationAnalyzer.features;
        if (features.pitch.length > 0) {
          const latestPitch = features.pitch[features.pitch.length - 1];
          const latestRms = features.rms.length > 0 ? features.rms[features.rms.length - 1] : { value: 0 };
          const analysis = pronunciationAnalyzer.analyzeStressPatterns();

          setRealTimeFeatures({
            pitch: latestPitch.frequency.toFixed(1),
            intensity: (latestRms.value * 100).toFixed(1),
            stressPattern: analysis.stressPattern
          });
        }
      }, 200);
    }

    return () => {
      if (interval) clearInterval(interval);
    };
  }, [isAnalyzingPronunciation, pronunciationAnalyzer]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (recordingTimerRef.current) {
        clearInterval(recordingTimerRef.current);
      }
      if (currentStreamRef.current) {
        currentStreamRef.current.getTracks().forEach(track => track.stop());
      }
      if (pronunciationAnalyzer) {
        pronunciationAnalyzer.stopAnalysis();
      }
    };
  }, [pronunciationAnalyzer]);

  // Timer functions
  const startRecordingTimer = useCallback(() => {
    setRecordingTime(0);
    recordingTimerRef.current = setInterval(() => {
      setRecordingTime(prev => prev + 1);
    }, 1000);
  }, []);

  const stopRecordingTimer = useCallback(() => {
    if (recordingTimerRef.current) {
      clearInterval(recordingTimerRef.current);
      recordingTimerRef.current = null;
    }
  }, []);

  const formatTime = useCallback((seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  }, []);

  // Function to create new test
  const createNewTest = (criteria = {}) => {
    let test;
    if (criteria.mode === 'custom' && Object.keys(criteria).length > 1) {
      test = createCustomTest(criteria);
    } else {
      test = createRandomTest();
    }

    setCurrentTest(test);
    setAnswers(Array(test.questions.length).fill(""));
    setCurrentQuestionIndex(0);
    setFeedbacks(null);
    setTestSummary(null);
    setShowResults(false);
    setError(null);
    setRecordingTime(0);
    setAudioFeatures({});
    setRealTimeFeatures({ pitch: 0, intensity: 0, stressPattern: '' });
    if (pronunciationAnalyzer) {
      pronunciationAnalyzer.reset();
    }
  };

  // Enhanced recording with pronunciation analysis
  const toggleRecording = async () => {
    if (recording) {
      // Stop recording
      recognitionRef.current?.stop();
      if (pronunciationAnalyzer && isAnalyzingPronunciation) {
        pronunciationAnalyzer.stopAnalysis();
        const features = pronunciationAnalyzer.getFeatures();
        setAudioFeatures(prev => ({
          ...prev,
          [currentQuestionIndex]: features
        }));
        setIsAnalyzingPronunciation(false);
      }
      if (currentStreamRef.current) {
        currentStreamRef.current.getTracks().forEach(track => track.stop());
      }
      return;
    }

    // Check browser support
    if (!("webkitSpeechRecognition" in window) && !("SpeechRecognition" in window)) {
      setError("Speech recognition is not supported in this browser. Please use Chrome or Edge.");
      return;
    }

    try {
      // Get microphone stream
      const stream = await navigator.mediaDevices.getUserMedia({
        audio: {
          echoCancellation: true,
          noiseSuppression: true,
          autoGainControl: false,
          sampleRate: 44100
        }
      });

      currentStreamRef.current = stream;

      // Start pronunciation analysis
      if (pronunciationAnalyzer) {
        try {
          pronunciationAnalyzer.reset();
          await pronunciationAnalyzer.startAnalysis(stream);
          setIsAnalyzingPronunciation(true);
        } catch (analysisError) {
          console.warn('Pronunciation analysis failed:', analysisError);
        }
      }

      // Setup speech recognition
      const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
      const recognition = new SpeechRecognition();

      recognition.lang = "en-US";
      recognition.continuous = true;
      recognition.interimResults = false;
      recognition.maxAlternatives = 1;

      recognition.onstart = () => {
        setRecording(true);
        setError(null);
        startRecordingTimer();
      };

      recognition.onresult = (event) => {
        const speechText = Array.from(event.results)
          .map((result) => result[0].transcript)
          .join(" ");

        setAnswers((prev) => {
          const newAnswers = [...prev];
          newAnswers[currentQuestionIndex] = speechText.trim();
          return newAnswers;
        });
      };

      recognition.onend = () => {
        setRecording(false);
        stopRecordingTimer();
      };

      recognition.onerror = (event) => {
        console.error("Speech recognition error:", event.error);
        setError(`Speech recognition error: ${event.error}. Please try again.`);
        setRecording(false);
        stopRecordingTimer();
        setIsAnalyzingPronunciation(false);
      };

      recognitionRef.current = recognition;
      recognition.start();

    } catch (err) {
      setError("Failed to access microphone. Please check your permissions and try again.");
      console.error("Microphone access error:", err);
    }
  };

  // Navigation functions
  const goNext = () => {
    if (currentQuestionIndex < currentTest.questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    }
  };

  const goPrev = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(currentQuestionIndex - 1);
    }
  };

  // Enhanced submit with pronunciation analysis
  const submitTest = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch("/api/analyze-batch", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          testId: currentTest.testId,
          questions: currentTest.questions,
          answers,
          sampleAnswers: currentTest.sampleAnswers,
          audioFeatures // Include pronunciation features
        }),
      });

      if (!response.ok) {
        throw new Error(`Server error: ${response.status}`);
      }

      const data = await response.json();
      setFeedbacks(data.feedbacks);
      setTestSummary(data.testSummary);
      setShowResults(true);
    } catch (err) {
      console.error("Analysis error:", err);
      setError("Error during analysis. Please check your connection and try again.");
    } finally {
      setLoading(false);
    }
  };

  // Reset function
  const resetTest = () => {
    createNewTest({ mode: 'random' });
  };

  // Band color function
  const getBandColor = (band) => {
    if (band >= 8.5) return "#2e7d32"; // Dark green
    if (band >= 7) return "#388e3c";   // Green
    if (band >= 6.5) return "#689f38"; // Light green
    if (band >= 6) return "#afb42b";   // Yellow green
    if (band >= 5.5) return "#f57f17"; // Dark yellow
    if (band >= 5) return "#ff8f00";   // Orange
    if (band >= 4) return "#f57c00";   // Dark orange
    return "#d32f2f";                  // Red
  };

  // Get available categories for custom test creation
  const getAvailableCategories = () => {
    const categories = [...new Set(questionBank.map(q => q.category))];
    return categories;
  };

  // Loading state
  if (!currentTest) {
    return (
      <div className="speech-evaluator loading">
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Initializing IELTS Speech Evaluator...</p>
          <p>Loading {questionBank.length} questions from question bank...</p>
        </div>
      </div>
    );
  }

  // Results display
  if (showResults && feedbacks) {
    return (
      <div className="speech-evaluator results-view">
        <div className="results-header">
          <h2> IELTS Speaking Test Results</h2>
          <div className="test-info">
            <span className="test-title">{currentTest.title}</span>
            <span className="questions-completed">{feedbacks.filter(f => f.overall > 0).length} of {currentTest.questions.length} questions completed</span>
          </div>
        </div>

        {testSummary && (
          <div className="overall-results" style={{ borderColor: getBandColor(testSummary.overall) }}>
            <h3>Overall Performance Summary</h3>

            <div className="band-scores-grid">
              <div className="overall-band">
                <div className="band-score" style={{ color: getBandColor(testSummary.overall) }}>
                  {testSummary.overall}
                </div>
                <div className="band-label">Overall Band</div>
              </div>

              <div className="criteria-bands">
                <div className="band-item">
                  <div className="band-score" style={{ color: getBandColor(testSummary.fluency) }}>
                    {testSummary.fluency}
                  </div>
                  <div className="band-label">Fluency & Coherence</div>
                </div>
                <div className="band-item">
                  <div className="band-score" style={{ color: getBandColor(testSummary.lexical) }}>
                    {testSummary.lexical}
                  </div>
                  <div className="band-label">Lexical Resource</div>
                </div>
                <div className="band-item">
                  <div className="band-score" style={{ color: getBandColor(testSummary.grammar) }}>
                    {testSummary.grammar}
                  </div>
                  <div className="band-label">Grammar</div>
                </div>
                <div className="band-item">
                  <div className="band-score" style={{ color: getBandColor(testSummary.pronunciation) }}>
                    {testSummary.pronunciation}
                  </div>
                  <div className="band-label">Pronunciation</div>
                </div>
              </div>
            </div>

            <div className="performance-stats">
              <div className="stat-item">
                <span className="stat-value">{testSummary.totalWords}</span>
                <span className="stat-label">Total Words</span>
              </div>
              <div className="stat-item">
                <span className="stat-value">{testSummary.totalErrors}</span>
                <span className="stat-label">Grammar Errors</span>
              </div>
              <div className="stat-item">
                <span className="stat-value">{Math.round((testSummary.stressAccuracy || 0.5) * 100)}%</span>
                <span className="stat-label">Stress Accuracy</span>
              </div>
              <div className="stat-item">
                <span className="stat-value">{Math.round((testSummary.intonationScore || 0.5) * 100)}%</span>
                <span className="stat-label">Intonation</span>
              </div>
            </div>
          </div>
        )}

        <div className="question-results">
          {feedbacks.map((feedback, i) => (
            <div key={i} className="question-result">
              <div className="question-header">
                <div>
                  <h4>Question {i + 1}</h4>
                  {currentTest.categories && (
                    <small className="question-category">Category: {currentTest.categories[i]} | Difficulty: {currentTest.difficulties[i]}</small>
                  )}
                </div>
                <div className="question-bands">
                  <span className="mini-band" style={{ backgroundColor: getBandColor(feedback.overall) }}>
                    {feedback.overall}
                  </span>
                </div>
              </div>
              <div className="feedback-content">
                {feedback.feedback.split('\n').map((line, idx) => (
                  <div key={idx} className="feedback-line">
                    {line.includes('**') ? (
                      <div dangerouslySetInnerHTML={{ 
                        __html: line.replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>') 
                      }} />
                    ) : (
                      <div>{line}</div>
                    )}
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>

        <div className="results-actions">
          <button onClick={resetTest} className="btn btn-primary">
             Take Another Random Test
          </button>
          <button onClick={() => window.print()} className="btn btn-secondary">
             Print Results
          </button>
        </div>
      </div>
    );
  }

  // Main test interface
  return (
    <div className="speech-evaluator">
      <div className="test-header">
        <div className="test-info">
          <h2>{currentTest.title}</h2>
          <div className="progress-info">
            <span className="question-counter">Question {currentQuestionIndex + 1} of {currentTest.questions.length}</span>
            <div className="progress-bar">
              <div 
                className="progress-fill" 
                style={{ width: `${((currentQuestionIndex + 1) / currentTest.questions.length) * 100}%` }}
              ></div>
            </div>
          </div>
        </div>
      </div>

      <div className="question-section">
        <div className="question-card">
          <div className="question-text">
            {currentTest.questions[currentQuestionIndex]}
          </div>
          <div className="question-metadata">
            <span className="recommended-time"> Recommended: 1-2 minutes</span>
            <span className="word-target"> Target: 100+ words</span>
            {currentTest.categories && (
              <>
                <span className="category"> {currentTest.categories[currentQuestionIndex]}</span>
                <span className="difficulty"> {currentTest.difficulties[currentQuestionIndex]}</span>
              </>
            )}
          </div>
        </div>
      </div>

      <div className="recording-section">
        <div className="recording-controls">
          <button 
            onClick={toggleRecording}
            disabled={loading}
            className={`record-btn ${recording ? 'recording' : ''}`}
          >
            {recording ? (
              <>
                <span className="record-icon"></span>
                Stop Recording ({formatTime(recordingTime)})
              </>
            ) : (
              <>
                <span className="record-icon"></span>
                Start Recording
              </>
            )}
          </button>
        </div>

        {recording && (
          <div className="real-time-analysis">
            <h4> Live Analysis</h4>
            <div className="analysis-grid">
              <div className="analysis-item">
                <span className="label">Pitch</span>
                <span className="value">{realTimeFeatures.pitch} Hz</span>
              </div>
              <div className="analysis-item">
                <span className="label">Intensity</span>
                <span className="value">{realTimeFeatures.intensity}%</span>
              </div>
              <div className="analysis-item">
                <span className="label">Pattern</span>
                <span className="stress-pattern">
                  {realTimeFeatures.stressPattern.split('').map((char, idx) => (
                    <span 
                      key={idx}
                      className={char === 'S' ? 'stressed' : 'unstressed'}
                    >
                      {char}
                    </span>
                  ))}
                </span>
              </div>
            </div>
          </div>
        )}

        {error && (
          <div className="error-message">
             {error}
          </div>
        )}
      </div>

      <div className="response-section">
        <label htmlFor="response-textarea">Your Response:</label>
        <textarea
          id="response-textarea"
          value={answers[currentQuestionIndex] || ""}
          onChange={(e) => {
            const newAnswers = [...answers];
            newAnswers[currentQuestionIndex] = e.target.value;
            setAnswers(newAnswers);
          }}
          placeholder="Your response will appear here as you speak, or you can type directly..."
          disabled={recording}
          rows={6}
        />

        <div className="response-stats">
          <span className="word-count">
            Words: {(answers[currentQuestionIndex] || "").split(/\s+/).filter(w => w.length > 0).length}
          </span>
          {answers[currentQuestionIndex] && (
            <span className="character-count">
              Characters: {answers[currentQuestionIndex].length}
            </span>
          )}
        </div>
      </div>

      <div className="navigation-section">
        <div className="nav-buttons">
          <button 
            onClick={goPrev}
            disabled={currentQuestionIndex === 0 || recording || loading}
            className="btn btn-secondary"
          >
             Previous
          </button>

          <div className="questions-status">
            {answers.map((answer, idx) => (
              <div 
                key={idx}
                className={`question-indicator ${idx === currentQuestionIndex ? 'current' : ''} ${answer.trim() ? 'completed' : ''}`}
                onClick={() => !recording && !loading && setCurrentQuestionIndex(idx)}
                title={currentTest.categories ? `${currentTest.categories[idx]} (${currentTest.difficulties[idx]})` : `Question ${idx + 1}`}
              >
                {idx + 1}
              </div>
            ))}
          </div>

          <button 
            onClick={goNext}
            disabled={currentQuestionIndex === currentTest.questions.length - 1 || recording || loading}
            className="btn btn-secondary"
          >
            Next 
          </button>
        </div>
      </div>

      {answers.some(answer => answer.trim().length > 0) && (
        <div className="submit-section">
          <div className="submit-info">
            <h4>Ready to get your results?</h4>
            <p>You've answered {answers.filter(a => a.trim().length > 0).length} of {currentTest.questions.length} questions.</p>
          </div>
          <button
            onClick={submitTest}
            disabled={loading}
            className="btn btn-primary submit-btn"
          >
            {loading ? (
              <>
                <span className="loading-spinner small"></span>
                Analyzing your responses...
              </>
            ) : (
              <>
                 Submit Test for Analysis
              </>
            )}
          </button>
        </div>
      )}

      <div className="test-options-section">
        <details>
          <summary> Test Options</summary>
          <div className="test-options-content">
            <h4>Create a Custom Test:</h4>
            <div className="custom-test-controls">
              <div className="control-group">
                <label>Category:</label>
                <select 
                  value={customCriteria.category || ''}
                  onChange={(e) => setCustomCriteria(prev => ({...prev, category: e.target.value}))}
                >
                  <option value="">Any Category</option>
                  {getAvailableCategories().map(cat => (
                    <option key={cat} value={cat}>{cat}</option>
                  ))}
                </select>
              </div>

              <div className="control-group">
                <label>Difficulty:</label>
                <select 
                  value={customCriteria.difficulty || ''}
                  onChange={(e) => setCustomCriteria(prev => ({...prev, difficulty: e.target.value}))}
                >
                  <option value="">Mixed</option>
                  <option value="easy">Easy</option>
                  <option value="medium">Medium</option>
                  <option value="hard">Hard</option>
                </select>
              </div>

              <div className="control-group">
                <label>Number of Questions:</label>
                <select 
                  value={customCriteria.numQuestions || 3}
                  onChange={(e) => setCustomCriteria(prev => ({...prev, numQuestions: parseInt(e.target.value)}))}
                >
                  <option value={3}>3 Questions</option>
                  <option value={5}>5 Questions</option>
                  <option value={7}>7 Questions</option>
                </select>
              </div>

              <button 
                onClick={() => createNewTest({...customCriteria, mode: 'custom'})}
                className="btn btn-secondary"
                disabled={recording || loading}
              >
                 Create Custom Test
              </button>
            </div>

            <div style={{marginTop: '15px'}}>
              <button 
                onClick={resetTest}
                className="btn btn-secondary"
                disabled={recording || loading}
              >
                 Generate Random Test
              </button>
            </div>

            <div className="question-bank-info" style={{marginTop: '15px', fontSize: '0.9rem', color: '#666'}}>
              <strong>Question Bank:</strong> {questionBank.length} total questions available
              <br />
              <strong>Categories:</strong> {getAvailableCategories().join(', ')}
            </div>
          </div>
        </details>
      </div>

      <div className="help-section">
        <details>
          <summary> Instructions & Tips</summary>
          <div className="help-content">
            <h4>How to use this evaluator:</h4>
            <ul>
              <li>Click "Start Recording" and speak your answer clearly</li>
              <li>You can also type your response directly</li>
              <li>Navigate between questions using the Previous/Next buttons or click question numbers</li>
              <li>Aim for detailed responses (100+ words per question)</li>
              <li>Submit when you've answered at least one question</li>
            </ul>

            <h4>For best pronunciation analysis:</h4>
            <ul>
              <li>Use a quiet environment</li>
              <li>Speak at a natural pace</li>
              <li>Ensure good microphone quality</li>
              <li>Use Chrome or Edge browsers</li>
            </ul>

            <h4>Question Categories Available:</h4>
            <ul>
              {getAvailableCategories().map(category => (
                <li key={category}>{category}</li>
              ))}
            </ul>
          </div>
        </details>
      </div>
    </div>
  );
}

export default SpeechEvaluator;
