// SpeechEvaluator.jsx - Final version with jsPDF and autoTable integration for PDF generation

import React, { useState, useRef, useEffect, useCallback } from "react";
import questionBank from "../data/questions-corrected";
import './SpeechEvaluator.css';
import { jsPDF } from "jspdf";
import autoTable from "jspdf-autotable";

jsPDF.API.autoTable = autoTable;

// Function to create IELTS test structure (unchanged)
const createIELTSTest = () => {
  const availableTopics = [...questionBank.part1Topics];
  const shuffledTopics = availableTopics.sort(() => Math.random() - 0.5);

  const introTopic = shuffledTopics[0];
  const introQuestions = [...introTopic.questions]
    .sort(() => Math.random() - 0.5)
    .slice(0, 2);

  const secondTopic = shuffledTopics[1];
  const secondQuestions = [...secondTopic.questions]
    .sort(() => Math.random() - 0.5)
    .slice(0, Math.random() > 0.5 ? 2 : 3);

  let part1Questions = [];
  introQuestions.forEach(q => {
    part1Questions.push({
      ...q,
      topicName: introTopic.topicName,
      part: 1,
      expectedDuration: 30,
    });
  });
  secondQuestions.forEach(q => {
    part1Questions.push({
      ...q,
      topicName: secondTopic.topicName,
      part: 1,
      expectedDuration: 30,
    });
  });

  const cueCards = [...questionBank.part2CueCards];
  const selectedCueCard = cueCards[Math.floor(Math.random() * cueCards.length)];
  const part2Question = {
    ...selectedCueCard,
    part: 2,
    expectedDuration: 120,
    preparationTime: 60,
  };

  const relatedDiscussion = questionBank.part3Discussions.find(
    disc => disc.relatedToPart2 === selectedCueCard.id
  );
  let part3Questions = [];
  if (relatedDiscussion) {
    const shuffledDiscQuestions = [...relatedDiscussion.questions].sort(() => Math.random() - 0.5);
    const numQuestions = Math.floor(Math.random() * 3) + 4;
    part3Questions = shuffledDiscQuestions.slice(0, numQuestions).map(q => ({
      ...q,
      part: 3,
      topicTheme: relatedDiscussion.topicTheme,
      expectedDuration: 60,
    }));
  }

  return {
    testId: `ielts_test_${Date.now()}`,
    title: `IELTS Speaking Test - ${new Date().toLocaleDateString()}`,
    testStructure: {
      part1: {
        name: "Part 1: Introduction & Interview",
        duration: "4-5 minutes",
        questions: part1Questions,
        topics: [introTopic.topicName, secondTopic.topicName],
      },
      part2: {
        name: "Part 2: Individual Long Turn",
        duration: "3-4 minutes",
        question: part2Question,
        cueCard: selectedCueCard.cueCard,
      },
      part3: {
        name: "Part 3: Two-way Discussion",
        duration: "4-5 minutes",
        questions: part3Questions,
        theme: relatedDiscussion?.topicTheme || "General Discussion",
      },
    },
    allQuestions: [
      ...part1Questions.map(q => ({ ...q, prompt: q.prompt })),
      { ...part2Question, prompt: part2Question.cueCard.mainPrompt },
      ...part3Questions.map(q => ({ ...q, prompt: q.prompt })),
    ],
    totalQuestions: part1Questions.length + 1 + part3Questions.length,
  };
};

// Main SpeechEvaluator component and all existing logic remain unchanged...

function SpeechEvaluator() {
  // All your existing state, refs, effect hooks, functions...

  // ... previously defined functions

  // Add PDF download function:
  const downloadCertificate = () => {
    if (!testSummary || !currentTest) return;
    const doc = new jsPDF();
    doc.setFontSize(20);
    doc.text("IELTS Speaking Test Certificate", 20, 30);

    doc.setFontSize(12);
    doc.text(`Candidate Name: ____________________________`, 20, 50);
    const testDate = new Date(parseInt(currentTest.testId.replace("ielts_test_", ""), 10));
    doc.text(`Test Date: ${testDate.toLocaleDateString()}`, 20, 65);

    doc.autoTable({
      startY: 75,
      head: [["Skill", "Band Score"]],
      body: [
        ["Fluency & Coherence", testSummary.fluency || "-"],
        ["Lexical Resource", testSummary.lexical || "-"],
        ["Grammar Range & Accuracy", testSummary.grammar || "-"],
        ["Pronunciation", testSummary.pronunciation || "-"],
        ["Overall Band Score", testSummary.overall || "-"],
      ],
    });

    doc.text("Congratulations! You have completed the IELTS Speaking Test.", 20, doc.lastAutoTable.finalY + 30);
    doc.text("Signature: ___________________", 20, doc.lastAutoTable.finalY + 50);

    doc.save(`IELTS_Test_Certificate_${testDate.toISOString()}.pdf`);
  };

  if (!isComponentReady) {
    return (
      <div className="loading-container">
        <h2>Loading IELTS Speaking Test...</h2>
        {error ? <div className="error-message"><p>{error}</p></div> : <p>Setting up authentic IELTS format with auto-recording...</p>}
      </div>
    );
  }

  if (showResults) {
    const part1Len = currentTest.testStructure.part1.questions.length;

    return (
      <div className="results-container">
        <div className="results-summary panel">
          <h2>IELTS Speaking Test Results</h2>
          <div className="band-grid">
            <div className="band-item">
              <strong>Fluency & Coherence</strong>
              <span style={{ color: getBandColor(testSummary?.fluency) }}>{testSummary?.fluency || "-"} / 9</span>
            </div>
            <div className="band-item">
              <strong>Lexical Resource</strong>
              <span style={{ color: getBandColor(testSummary?.lexical) }}>{testSummary?.lexical || "-"} / 9</span>
            </div>
            <div className="band-item">
              <strong>Grammar Accuracy</strong>
              <span style={{ color: getBandColor(testSummary?.grammar) }}>{testSummary?.grammar || "-"} / 9</span>
            </div>
            <div className="band-item">
              <strong>Pronunciation</strong>
              <span style={{ color: getBandColor(testSummary?.pronunciation) }}>{testSummary?.pronunciation || "-"} / 9</span>
            </div>
            <div className="band-item overall">
              <strong>Overall Band Score</strong>
              <span>{testSummary?.overall || "-"} / 9</span>
            </div>
          </div>
        </div>

        <div className="results-actions panel">
          <button onClick={downloadCertificate} className="btn-primary">📄 Download Certificate</button>
          <button onClick={resetTest} className="btn-secondary">🔄 Retake Test</button>
        </div>

        <div className="detailed-feedback panel">
          <h3>Detailed Question-by-Question Feedback</h3>
          {feedbacks.map((fb, idx) => {
            const part = idx < part1Len ? 1 : idx === part1Len ? 2 : 3;
            return (
              <details key={idx} className="feedback-item">
                <summary>Question {idx + 1} (Part {part}) – Overall: {fb.overall || "-"} / 9</summary>
                <pre>{fb.feedback}</pre>
              </details>
            );
          })}
        </div>
      </div>
    );
  }

  // Render test UI (Parts 1-3) here unchanged, preserving your previous code

  return (
    <div className="speech-evaluator">
      {/* Include your existing JSX test UI here unchanged */}
    </div>
  );
}

export default SpeechEvaluator;
