    return Math.sqrt(sum / timeData.length);
  }
  calculateZCR(timeData) {
    let zeroCrossings = 0;
    for (let i = 1; i < timeData.length; i++) {
      if (timeData[i] >= 0 !== timeData[i - 1] >= 0) {
