import React, { useState, useEffect, useRef } from "react";
import { Mic, Square, ArrowRight, ArrowLeft } from "lucide-react";

const SpeechEvaluator = () => {
  const [currentPart, setCurrentPart] = useState(1);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [recording, setRecording] = useState(false);
  const [transcripts, setTranscripts] = useState({});
  const [preparationTime, setPreparationTime] = useState(0);
  const [isPreparingPart2, setIsPreparingPart2] = useState(false);
  const [autoStartRecording, setAutoStartRecording] = useState(false);
  const mediaRecorderRef = useRef(null);
  const recognitionRef = useRef(null);

  const parts = {
    1: {
      title: "Part 1: Introduction & Interview",
      questions: [
        "What is your full name?",
        "Where are you from?",
        "Do you work or study?",
      ],
    },
    2: {
      title: "Part 2: Cue Card",
      questions: [
        "Describe a memorable journey you have taken.",
      ],
    },
    3: {
      title: "Part 3: Discussion",
      questions: [
        "How has travel changed compared to the past?",
        "What are the pros and cons of tourism?",
      ],
    },
  };

  const currentPartInfo = parts[currentPart];

  // Preparation timer for Part 2
  useEffect(() => {
    let timer;
    if (isPreparingPart2 && preparationTime > 0) {
      timer = setTimeout(() => {
        setPreparationTime(preparationTime - 1);
      }, 1000);
    } else if (isPreparingPart2 && preparationTime === 0) {
      setIsPreparingPart2(false);
      setAutoStartRecording(true);
    }
    return () => clearTimeout(timer);
  }, [isPreparingPart2, preparationTime]);

  // Auto-start recording when preparation ends
  useEffect(() => {
    if (autoStartRecording && !isPreparingPart2 && preparationTime === 0 && !recording) {
      const autoStartTimeout = setTimeout(() => {
        startRecording();
        setAutoStartRecording(false);
      }, 500);
      return () => clearTimeout(autoStartTimeout);
    }
  }, [autoStartRecording, isPreparingPart2, preparationTime, recording]);

  const startPreparationTimer = () => {
    setPreparationTime(60);
    setIsPreparingPart2(true);
  };

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mediaRecorder = new MediaRecorder(stream);
      mediaRecorderRef.current = mediaRecorder;
      mediaRecorder.start();
      setRecording(true);

      const SpeechRecognition =
        window.SpeechRecognition || window.webkitSpeechRecognition;
      if (SpeechRecognition) {
        const recognition = new SpeechRecognition();
        recognition.continuous = true;
        recognition.interimResults = true;
        recognition.lang = "en-US";

        recognition.onresult = (event) => {
          const transcript = Array.from(event.results)
            .map((result) => result[0].transcript)
            .join(" ");
          setTranscripts((prev) => ({
            ...prev,
            [currentPart + "-" + currentQuestionIndex]: transcript,
          }));
        };

        recognitionRef.current = recognition;
        recognition.start();
      }
    } catch (error) {
      console.error("Error accessing microphone", error);
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current) {
      mediaRecorderRef.current.stop();
    }
    if (recognitionRef.current) {
      recognitionRef.current.stop();
    }
    setRecording(false);
  };

  const goNext = () => {
    if (currentQuestionIndex < currentPartInfo.questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else if (currentPart < 3) {
      setCurrentPart(currentPart + 1);
      setCurrentQuestionIndex(0);
    }
  };

  const goPrev = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(currentQuestionIndex - 1);
    } else if (currentPart > 1) {
      const prevPart = currentPart - 1;
      setCurrentPart(prevPart);
      setCurrentQuestionIndex(parts[prevPart].questions.length - 1);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="max-w-3xl mx-auto space-y-6">
        <div className="shadow-lg rounded-lg bg-white p-6 space-y-6">
          <h2 className="text-xl font-bold">{currentPartInfo.title}</h2>

          {/* Question Display */}
          {(currentPartInfo?.part !== 2 || isPreparingPart2) && (
            <div className="question-section">
              <p className="text-lg font-medium mb-4">
                {currentPartInfo.questions[currentQuestionIndex]}
              </p>
            </div>
          )}

          {/* Preparation Section for Part 2 */}
          {currentPart === 2 && !recording && !isPreparingPart2 && preparationTime === 0 && (
            <div className="space-y-4">
              <button onClick={startPreparationTimer} className="px-4 py-2 bg-blue-600 text-white rounded-lg">
                Start Preparation (1 minute)
              </button>
            </div>
          )}

          {isPreparingPart2 && (
            <div className="space-y-2">
              <p className="text-red-500 font-medium">Preparation Time: {preparationTime}s</p>
              <textarea
                className="w-full border p-2 rounded"
                rows="4"
                placeholder="Take notes here during preparation..."
              ></textarea>
            </div>
          )}

          {/* Recording Section */}
          {recording && (
            <div className="space-y-4">
              <p className="text-green-600 font-medium">Recording in progress...</p>
              <button onClick={stopRecording} className="px-4 py-2 bg-red-600 text-white rounded-lg flex items-center">
                <Square className="w-4 h-4 mr-2" /> Stop Recording
              </button>
            </div>
          )}

          {/* Transcript Display */}
          {transcripts[currentPart + "-" + currentQuestionIndex] && (
            <div className="mt-4 p-3 border rounded bg-gray-100">
              <p className="text-sm text-gray-700">Transcript:</p>
              <p className="text-gray-900">
                {transcripts[currentPart + "-" + currentQuestionIndex]}
              </p>
            </div>
          )}

          {/* Navigation Buttons */}
          <div className="flex justify-between pt-4">
            <button
              onClick={goPrev}
              disabled={currentQuestionIndex === 0 || isPreparingPart2 || recording}
              className="px-4 py-2 bg-gray-400 text-white rounded-lg flex items-center disabled:opacity-50"
            >
              <ArrowLeft className="w-4 h-4 mr-2" /> Previous
            </button>
            {currentPart < 3 || currentQuestionIndex < currentPartInfo.questions.length - 1 ? (
              <button
                onClick={goNext}
                disabled={isPreparingPart2 || recording}
                className="px-4 py-2 bg-blue-600 text-white rounded-lg flex items-center disabled:opacity-50"
              >
                Next <ArrowRight className="w-4 h-4 ml-2" />
              </button>
            ) : (
              <button
                disabled={isPreparingPart2 || recording}
                className="px-4 py-2 bg-green-600 text-white rounded-lg disabled:opacity-50"
              >
                Submit
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default SpeechEvaluator;
