import React, { useState, useRef, useEffect, useCallback } from "react";
import questionBank from "../data/questions-corrected";
import sampleAnswers from "../data/sampleAnswers";
import './SpeechEvaluator.css';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, BarElement, CategoryScale, LinearScale, Title, Tooltip, Legend } from 'chart.js';
import jsPDF from 'jspdf';

// Register Chart.js components
ChartJS.register(BarElement, CategoryScale, LinearScale, Title, Tooltip, Legend);

const getBandColor = (band) => {
  if (band >= 8.5) return "#2e7d32";
  if (band >= 7) return "#388e3c";
  if (band >= 6.5) return "#689f38";
  if (band >= 6) return "#afb42b";
  if (band >= 5.5) return "#f57f17";
  if (band >= 5) return "#ff8f00";
  if (band >= 4) return "#d32f2f";
  return "#d32f2f";
};

const createIELTSTest = () => {
  const availableTopics = [...questionBank.part1Topics];
  const shuffledTopics = availableTopics.sort(() => Math.random() - 0.5);

  const introTopic = shuffledTopics[0];
  const introQuestions = [...introTopic.questions].sort(() => Math.random() - 0.5).slice(0, 2);
  const secondTopic = shuffledTopics[1];
  const secondQuestionsCount = Math.random() > 0.5 ? 2 : 3;
  const secondQuestions = [...secondTopic.questions].sort(() => Math.random() - 0.5).slice(0, secondQuestionsCount);

  let part1Questions = [];
  introQuestions.forEach(q => part1Questions.push({ ...q, topicName: introTopic.topicName, part: 1, expectedDuration: 30 }));
  secondQuestions.forEach(q => part1Questions.push({ ...q, topicName: secondTopic.topicName, part: 1, expectedDuration: 30 }));

  const cueCards = [...questionBank.part2CueCards];
  const selectedCueCard = cueCards[Math.floor(Math.random() * cueCards.length)];
  const part2Question = { ...selectedCueCard, part: 2, expectedDuration: 120, preparationTime: 60 };

  const relatedDiscussion = questionBank.part3Discussions.find(disc => disc.relatedToPart2 === selectedCueCard.id);
  let part3Questions = [];
  if (relatedDiscussion) {
    const shuffledDiscQuestions = [...relatedDiscussion.questions].sort(() => Math.random() - 0.5);
    const numQuestions = Math.floor(Math.random() * 3) + 4;
    part3Questions = shuffledDiscQuestions.slice(0, numQuestions).map(q => ({ ...q, part: 3, topicTheme: relatedDiscussion.topicTheme, expectedDuration: 60 }));
  }

  const allQuestions = [
    ...part1Questions.map(q => ({ ...q, prompt: q.prompt })),
    { ...part2Question, prompt: part2Question.cueCard.mainPrompt },
    ...part3Questions.map(q => ({ ...q, prompt: q.prompt })),
  ];

  return {
    testId: `ielts_test_${Date.now()}`,
    title: `IELTS Speaking Test - ${new Date().toLocaleDateString()}`,
    testStructure: {
      part1: { name: "Part 1: Introduction & Interview", duration: "4-5 minutes", questions: part1Questions, topics: [introTopic.topicName, secondTopic.topicName] },
      part2: { name: "Part 2: Individual Long Turn", duration: "3-4 minutes", question: part2Question, cueCard: selectedCueCard.cueCard },
      part3: { name: "Part 3: Two-way Discussion", duration: "4-5 minutes", questions: part3Questions, theme: relatedDiscussion?.topicTheme || "General Discussion" }
    },
    allQuestions,
    totalQuestions: allQuestions.length,
  };
};

// Pronunciation Analyzer (class unchanged, omitted here for brevity)

function SpeechEvaluator() {
  // State declarations (as before)
  const [currentTest, setCurrentTest] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [currentPart, setCurrentPart] = useState(1);
  const [answers, setAnswers] = useState([]);
  const [recording, setRecording] = useState(false);
  const [loading, setLoading] = useState(false);
  const [feedbacks, setFeedbacks] = useState(null);
  const [testSummary, setTestSummary] = useState(null);
  const [error, setError] = useState(null);
  const [recordingTime, setRecordingTime] = useState(0);
  const [showResults, setShowResults] = useState(false);
  const [preparationTime, setPreparationTime] = useState(0);
  const [isPreparingPart2, setIsPreparingPart2] = useState(false);
  const [preparationNotes, setPreparationNotes] = useState("");
  const [autoStartRecording, setAutoStartRecording] = useState(false);
  const [isComponentReady, setIsComponentReady] = useState(false);
  const [initializationError, setInitializationError] = useState(null);
  const [pronunciationAnalyzer, setPronunciationAnalyzer] = useState(null);
  const [audioFeatures, setAudioFeatures] = useState({});
  const [isAnalyzingPronunciation, setIsAnalyzingPronunciation] = useState(false);

  // Refs as before: recognitionRef, recordingTimerRef, etc.

  useEffect(() => {
    const initializeComponent = async () => {
      try {
        const test = createIELTSTest();
        setCurrentTest(test);
        setAnswers(Array(test.totalQuestions).fill(""));
        const analyzer = new SimplePronunciationAnalyzer();
        setPronunciationAnalyzer(analyzer);
        setIsComponentReady(true);
        setInitializationError(null);
      } catch (error) {
        console.error('Component initialization failed:', error);
        setInitializationError('Failed to initialize speech evaluator. Please refresh the page.');
        setIsComponentReady(true);
      }
    };
    initializeComponent();
  }, []);

  if (!isComponentReady) {
    return (
      <div className="loading-container">
        {initializationError || <h2>Setting up authentic IELTS format with auto-recording...</h2>}
      </div>
    );
  }

  if (!currentTest) return null;

  // Band Chart data
  const bandData = {
    labels: ['Part 1', 'Part 2', 'Part 3'],
    datasets: [{
      label: 'IELTS Bands',
      data: [
        testSummary?.part1Band || 0,
        testSummary?.part2Band || 0,
        testSummary?.part3Band || 0,
      ],
      backgroundColor: [
        getBandColor(testSummary?.part1Band),
        getBandColor(testSummary?.part2Band),
        getBandColor(testSummary?.part3Band),
      ],
    }],
  };

  // Export PDF function (same as before)

  // Other event handlers: startRecording, stopRecording, toggleRecording, navigation, submitTest, resetTest ‚Äî keep your existing implementations

  // Current part info logic as before
  const currentPartInfo = (() => {
    const part1Length = currentTest.testStructure.part1.questions.length;
    const part2Index = part1Length;
    if(currentQuestionIndex < part1Length) return { part: 1, info: currentTest.testStructure.part1 };
    if(currentQuestionIndex === part2Index) return { part: 2, info: currentTest.testStructure.part2 };
    return { part: 3, info: currentTest.testStructure.part3 };
  })();

  return (
    <div className="speech-evaluator">
      {/* Your original test header & navigation, buttons, progress, question display, recording controls here using your CSS classes */}

      {!showResults && (
        <>
          {/* Part and question info */}
          <div className="test-header">
            <h1>{currentTest.title}</h1>
            <div className="test-info">
              <div className="test-id">Test ID: {currentTest.testId}</div>
              <div className="progress">Progress: {answers.filter(a => a.trim() !== '').length} / {currentTest.totalQuestions}</div>
            </div>
          </div>

          <div className="test-content">
            <div className="part-indicator">
              <h2>{currentPartInfo.info.name}</h2>
              <p className="part-duration">Duration: {currentPartInfo.info.duration}</p>
              {currentPartInfo.part ===1 && <p className="part-description">Topics: {currentPartInfo.info.topics.join(", ")}</p>}
              {currentPartInfo.part ===2 && (
                <div className="part2-preparation-intro">
                  <h3>Part 2: Preparation</h3>
                  <p>üìù 1-minute preparation ‚Üí üé§ Auto-recording starts ‚Üí üó£Ô∏è Speak for 1-2 minutes</p>
                </div>
              )}
              {currentPartInfo.part ===3 && <p className="part-description">Theme: {currentPartInfo.info.theme}</p>}
            </div>

            {/* Question display with question number, prompt, follow-ups etc. */}
            <div className="question-section">
              <div className="question-header">
                <span className="question-number">Q{currentQuestionIndex + 1}</span>
                {currentPartInfo.part === 1 && <span className="topic-badge">{currentTest.allQuestions[currentQuestionIndex].topicName}</span>}
              </div>

              <div className="question-content">
                <h3>{currentTest.allQuestions[currentQuestionIndex].prompt}</h3>
                {/* Your answer recording textarea or display here */}
              </div>

              <div className="duration-guide">
                <span>Approx. {currentTest.allQuestions[currentQuestionIndex].expectedDuration || 30} seconds per answer</span>
              </div>
            </div>

            <div className="recording-section">
              {/* Recording buttons styled using your CSS btn classes */}
              <button onClick={toggleRecording} className={`record-button ${recording ? 'recording' : ''}`}>
                {recording ? 'Stop Recording' : 'Start Recording'}
              </button>
            </div>

            <div className="navigation-section">
              <div className="nav-buttons">
                <button onClick={() => setCurrentQuestionIndex(currentQuestionIndex - 1)} disabled={currentQuestionIndex === 0} className="btn btn-secondary">Previous</button>
                <button onClick={() => setCurrentQuestionIndex(currentQuestionIndex + 1)} disabled={currentQuestionIndex >= currentTest.totalQuestions - 1} className="btn btn-secondary">Next</button>
                <button onClick={submitTest} disabled={loading || answers.filter(a => a.trim() !== '').length < currentTest.totalQuestions} className="btn btn-primary">Submit Test</button>
              </div>

              <div className="progress-info">
                <p>Speaking Time: {formatTime(recordingTime)}</p>
              </div>

              {error && <div className="error-message">{error}</div>}
            </div>
          </div>
        </>
      )}

      {showResults && (
        <div className="results-container">
          <h2>IELTS Speaking Results</h2>

          <div className="test-summary">
            <div className="band-scores-grid">
              <div className="band-score" style={{borderLeftColor: getBandColor(testSummary?.part1Band)}}>
                <span>Part 1 Band</span>
                <span>{testSummary?.part1Band || '-'}</span>
              </div>
              <div className="band-score" style={{borderLeftColor: getBandColor(testSummary?.part2Band)}}>
                <span>Part 2 Band</span>
                <span>{testSummary?.part2Band || '-'}</span>
              </div>
              <div className="band-score" style={{borderLeftColor: getBandColor(testSummary?.part3Band)}}>
                <span>Part 3 Band</span>
                <span>{testSummary?.part3Band || '-'}</span>
              </div>
              <div className="band-score overall">
                <span>Overall Band</span>
                <span>{testSummary?.overallBand || '-'}</span>
              </div>
            </div>
          </div>

          <div className="band-chart-wrapper" style={{maxWidth: '450px', margin: '30px auto'}}>
            <Bar data={bandData} options={{
              scales: { y: {min: 0, max: 9, ticks: {stepSize: 0.5} }},
              plugins: {legend: {display: false}}
            }} />
          </div>

          <button className="btn btn-primary export-pdf-btn" onClick={() => {
            if(!testSummary) return;
            const doc = new jsPDF();
            doc.setFontSize(18);
            doc.text('IELTS Speaking Test Feedback', 10, 10);
            doc.setFontSize(14);
            doc.text(`Part 1 Band: ${testSummary.part1Band}`, 10, 30);
            doc.text(`Part 2 Band: ${testSummary.part2Band}`, 10, 40);
            doc.text(`Part 3 Band: ${testSummary.part3Band}`, 10, 50);
            doc.text(`Overall Band: ${testSummary.overallBand}`, 10, 60);

            feedbacks?.forEach((fb, i) => {
              let y = 70 + i*30;
              doc.text(`Q${i + 1}: ${fb.question}`, 10, y);
              doc.text(`Your Answer: ${fb.answer}`, 10, y + 10);
              doc.text(`Feedback: ${fb.feedback}`, 10, y + 20);
              const sample = sampleAnswers.find(sa => sa.question === fb.question)?.sample;
              if(sample) {
                doc.text(`Sample Answer: ${sample}`, 10, y + 30);
              }
              if(y > 270) {
                doc.addPage();
                y = 20;
              }
            });

            doc.save('IELTS-Speaking-Feedback.pdf');
          }}>Export Feedback as PDF</button>

          <div className="detailed-feedback">
            {feedbacks?.map((fb, i) => (
              <div key={i} className="feedback-section">
                <h4>Question {i + 1}: {fb.question}</h4>
                <p><strong>Your Answer:</strong> {fb.answer}</p>
                <p><strong>Feedback:</strong> {fb.feedback}</p>
                <p className="sample-answer"><strong>Sample Answer:</strong> {sampleAnswers.find(sa => sa.question === fb.question)?.sample || "Sample answer coming soon."}</p>
              </div>
            ))}
          </div>

          <button className="btn btn-secondary retake-btn" onClick={() => {
            setShowResults(false);
            setFeedbacks(null);
            setTestSummary(null);
            setCurrentQuestionIndex(0);
            setAnswers(Array(currentTest.totalQuestions).fill(''));
            setCurrentPart(1);
          }}>Retake Test</button>
        </div>
      )}

    </div>
  );
}

export default SpeechEvaluator;
