import React, { useState, useRef, useEffect, useCallback } from "react";
import questionBank from "../data/questions-corrected";
import "./SpeechEvaluator.css";
import "./auto-record-styles.css";

// Build a real IELTS test
const createIELTSTest = () => {
  const topics = [...questionBank.part1Topics].sort(() => Math.random() - 0.5);
  const [first, second] = topics;
  const introQs = [...first.questions].sort(() => Math.random() - 0.5).slice(0, 4);
  const secondQs = [...second.questions].sort(() => Math.random() - 0.5).slice(0, Math.random() > 0.5 ? 3 : 4);
  const part1Qs = [
    ...introQs.map(q => ({ ...q, topicName: first.topicName, part: 1, expectedDuration: 30 })),
    ...secondQs.map(q => ({ ...q, topicName: second.topicName, part: 1, expectedDuration: 30 }))
  ];

  const cue = questionBank.part2CueCards[Math.floor(Math.random() * questionBank.part2CueCards.length)];
  const part2Q = { ...cue, part: 2, expectedDuration: 120, preparationTime: 60 };

  const disc = questionBank.part3Discussions.find(d => d.relatedToPart2 === cue.id) || { questions: [] };
  const part3Qs = [...disc.questions].sort(() => Math.random() - 0.5).slice(0, Math.floor(Math.random() * 3) + 4)
    .map(q => ({ ...q, part: 3, expectedDuration: 60 }));

  return {
    testId: `ielts_${Date.now()}`,
    title: `IELTS Speaking Test ‚Äì ${new Date().toLocaleDateString()}`,
    structure: {
      part1: { name: "Part 1", duration: "4‚Äì5 min", questions: part1Qs, topics: [first.topicName, second.topicName] },
      part2: { name: "Part 2", duration: "3‚Äì4 min", question: part2Q, cueCard: cue.cueCard },
      part3: { name: "Part 3", duration: "4‚Äì5 min", questions: part3Qs }
    },
    allQuestions: [
      ...part1Qs.map(q => ({ prompt: q.prompt, ...q })),
      { prompt: part2Q.cueCard.mainPrompt, ...part2Q },
      ...part3Qs.map(q => ({ prompt: q.prompt, ...q }))
    ],
    total: part1Qs.length + 1 + part3Qs.length
  };
};

class SimplePronunciationAnalyzer {
  // ... same as before ...
}

export default function SpeechEvaluator() {
  const [test, setTest] = useState(null);
  const [idx, setIdx] = useState(0);
  const [recording, setRecording] = useState(false);
  const [timer, setTimer] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [prepTime, setPrepTime] = useState(0);
  const [preparing, setPreparing] = useState(false);
  const [autoStart, setAutoStart] = useState(false);

  const recogRef = useRef(null);
  const recTimerRef = useRef(null);
  const prepRef = useRef(null);
  const streamRef = useRef(null);
  const analyzerRef = useRef(null);

  useEffect(() => {
    const init = async () => {
      const newTest = createIELTSTest();
      setTest(newTest);
      setAnswers(Array(newTest.total).fill(""));
      analyzerRef.current = new SimplePronunciationAnalyzer();
    };
    init();
  }, []);

  // Auto-record start after prep finishes
  useEffect(() => {
    if (autoStart && !preparing && prepTime === 0) {
      const t = setTimeout(() => {
        startRecording();
        setAutoStart(false);
      }, 500);
      return () => clearTimeout(t);
    }
  }, [autoStart, preparing, prepTime]);

  const formatTime = s => {
    const m = Math.floor(s / 60), sec = s % 60;
    return `${m}:${sec.toString().padStart(2, "0")}`;
  };

  const startPrep = () => {
    setPreparing(true);
    setPrepTime(60);
    setAutoStart(true);
    prepRef.current = setInterval(() => {
      setPrepTime(p => {
        if (p <= 1) {
          clearInterval(prepRef.current);
          setPreparing(false);
          return 0;
        }
        return p - 1;
      });
    }, 1000);
  };

  const goNext = () => {
    if (idx < test.total - 1) setIdx(idx + 1);
  };

  const stopRecording = () => {
    recogRef.current?.stop();
    analyzerRef.current?.stopAnalysis();
    streamRef.current?.getTracks().forEach(t => t.stop());
    setRecording(false);
    clearInterval(recTimerRef.current);
    setTimeout(goNext, 200);
  };

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      streamRef.current = stream;
      await analyzerRef.current.initialize();
      await analyzerRef.current.startAnalysis(stream);

      const SpeechRec = window.SpeechRecognition || window.webkitSpeechRecognition;
      const rec = new SpeechRec();
      rec.lang = "en-US"; rec.continuous = true; rec.interimResults = false;
      rec.onstart = () => {
        setRecording(true);
        setTimer(0);
        recTimerRef.current = setInterval(() => setTimer(t => t + 1), 1000);
      };
      rec.onresult = e => {
        const text = Array.from(e.results).map(r => r[0].transcript).join(" ");
        setAnswers(a => { a[idx] = text.trim(); return [...a]; });
      };
      rec.onend = stopRecording;
      rec.onerror = () => stopRecording();
      recogRef.current = rec;
      rec.start();
    } catch {
      // handle error
    }
  };

  if (!test) return <div>Loading...</div>;

  const part1Len = test.structure.part1.questions.length;
  const part2Idx = part1Len;
  const part = idx < part1Len ? 1 : idx === part2Idx ? 2 : 3;
  const q = test.allQuestions[idx];

  return (
    <div className="speech-evaluator">
      {part === 2 && !preparing && timer === 0 && !recording && (
        <div className="part2-preparation-intro">
          <h3>Part 2: Preparation</h3>
          <button onClick={startPrep} className="btn-primary">Start 1-min Prep</button>
        </div>
      )}

      {preparing && (
        <div className="preparation-phase">
          <h3>Prep: {formatTime(prepTime)}</h3>
          <div className="cue-card">
            <h4>{q.cueCard.mainPrompt}</h4>
            <ul>{q.cueCard.bulletPoints.map((b,i)=><li key={i}>{b}</li>)}</ul>
          </div>
        </div>
      )}

      {!preparing && (
        <div className="question-section">
          {part === 2 ? (
            <div className="cue-card">
              <h3>{q.cueCard.mainPrompt}</h3>
              <ul>{q.cueCard.bulletPoints.map((b,i)=><li key={i}>{b}</li>)}</ul>
            </div>
          ) : (
            <h3>{q.prompt}</h3>
          )}
        </div>
      )}

      {recording && (
        <div className="auto-recording-status">
          <span className="recording-dot">‚óè</span>
          <span>Recording: {formatTime(timer)}</span>
          <button onClick={stopRecording} className="record-button recording">‚èπÔ∏è Stop</button>
        </div>
      )}

      {!recording && part !== 2 && (
        <button onClick={startRecording} className="record-button">üé§ Start Recording</button>
      )}

      <textarea
        className="answer-section"
        value={answers[idx] || ""}
        onChange={e => setAnswers(a => { a[idx] = e.target.value; return [...a]; })}
        disabled={recording}
        placeholder="Your response..."
      />

      {/* Navigation hidden via CSS (.nav-buttons { display: none; }) */}
      <div className="navigation-section">
        <div className="nav-buttons">
          <button onClick={() => idx>0&&setIdx(idx-1)}>‚Üê Prev</button>
          <button onClick={goNext}>Next ‚Üí</button>
        </div>
      </div>
    </div>
  );
}
