// Enhanced SpeechEvaluator.jsx ‚Äì Auto-Start Recording, Auto-Advance, Hide Nav Buttons

import React, { useState, useRef, useEffect, useCallback } from "react";
import questionBank from "../data/questions-corrected";
import "./SpeechEvaluator.css";
import "./auto-record-styles.css";

// Build authentic IELTS test structure
const createIELTSTest = () => {
  const topics = [...questionBank.part1Topics].sort(() => Math.random() - 0.5);
  const [first, second] = topics;
  const introQs = [...first.questions].sort(() => Math.random() - 0.5).slice(0, 4);
  const secondQs = [...second.questions]
    .sort(() => Math.random() - 0.5)
    .slice(0, Math.random() > 0.5 ? 3 : 4);
  const part1Qs = [
    ...introQs.map((q) => ({ ...q, topicName: first.topicName, part: 1, expectedDuration: 30 })),
    ...secondQs.map((q) => ({ ...q, topicName: second.topicName, part: 1, expectedDuration: 30 })),
  ];

  const cue =
    questionBank.part2CueCards[
      Math.floor(Math.random() * questionBank.part2CueCards.length)
    ];
  const part2Q = { ...cue, part: 2, expectedDuration: 120, preparationTime: 60 };

  const disc =
    questionBank.part3Discussions.find((d) => d.relatedToPart2 === cue.id) || { questions: [] };
  const part3Qs = [...disc.questions]
    .sort(() => Math.random() - 0.5)
    .slice(0, Math.floor(Math.random() * 3) + 4)
    .map((q) => ({ ...q, part: 3, expectedDuration: 60 }));

  return {
    testId: `ielts_test_${Date.now()}`,
    title: `IELTS Speaking Test ‚Äì ${new Date().toLocaleDateString()}`,
    testStructure: {
      part1: { name: "Part 1", duration: "4‚Äì5 min", questions: part1Qs, topics: [first.topicName, second.topicName] },
      part2: { name: "Part 2", duration: "3‚Äì4 min", question: part2Q, cueCard: cue.cueCard },
      part3: { name: "Part 3", duration: "4‚Äì5 min", questions: part3Qs },
    },
    allQuestions: [
      ...part1Qs.map((q) => ({ prompt: q.prompt, ...q })),
      { prompt: part2Q.cueCard.mainPrompt, ...part2Q },
      ...part3Qs.map((q) => ({ prompt: q.prompt, ...q })),
    ],
    totalQuestions: part1Qs.length + 1 + part3Qs.length,
  };
};

// Pronunciation analyzer (unchanged)
class SimplePronunciationAnalyzer {
  // ... same implementation ...
}

export default function SpeechEvaluator() {
  const [currentTest, setCurrentTest] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [recording, setRecording] = useState(false);
  const [recordingTime, setRecordingTime] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [preparationTime, setPreparationTime] = useState(0);
  const [isPreparingPart2, setIsPreparingPart2] = useState(false);
  const [autoStartRecording, setAutoStartRecording] = useState(false);

  const recognitionRef = useRef(null);
  const recordingTimerRef = useRef(null);
  const preparationTimerRef = useRef(null);
  const currentStreamRef = useRef(null);
  const pronunciationAnalyzerRef = useRef(null);

  // Initialize test and analyzer
  useEffect(() => {
    const init = () => {
      const test = createIELTSTest();
      setCurrentTest(test);
      setAnswers(Array(test.totalQuestions).fill(""));
      pronunciationAnalyzerRef.current = new SimplePronunciationAnalyzer();
    };
    init();
  }, []);

  // Auto-start after preparation
  useEffect(() => {
    if (autoStartRecording && !isPreparingPart2 && preparationTime === 0) {
      const t = setTimeout(() => {
        startRecording();
        setAutoStartRecording(false);
      }, 500);
      return () => clearTimeout(t);
    }
  }, [autoStartRecording, isPreparingPart2, preparationTime]);

  const formatTime = (sec) => {
    const m = Math.floor(sec / 60),
      s = sec % 60;
    return `${m}:${s.toString().padStart(2, "0")}`;
  };

  // Part 2 preparation
  const startPart2Preparation = () => {
    setIsPreparingPart2(true);
    setPreparationTime(60);
    setAutoStartRecording(true);
    preparationTimerRef.current = setInterval(() => {
      setPreparationTime((t) => {
        if (t <= 1) {
          clearInterval(preparationTimerRef.current);
          setIsPreparingPart2(false);
          return 0;
        }
        return t - 1;
      });
    }, 1000);
  };

  // Recording control
  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      currentStreamRef.current = stream;
      await pronunciationAnalyzerRef.current.initialize();
      await pronunciationAnalyzerRef.current.startAnalysis(stream);

      const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
      const recognition = new SpeechRecognition();
      recognition.lang = "en-US";
      recognition.continuous = true;
      recognition.interimResults = false;

      recognition.onstart = () => {
        setRecording(true);
        setRecordingTime(0);
        recordingTimerRef.current = setInterval(
          () => setRecordingTime((t) => t + 1),
          1000
        );
      };
      recognition.onresult = (e) => {
        const text = Array.from(e.results)
          .map((r) => r[0].transcript)
          .join(" ");
        setAnswers((a) => {
          a[currentQuestionIndex] = text.trim();
          return [...a];
        });
      };
      recognition.onend = stopRecording;
      recognition.onerror = stopRecording;

      recognitionRef.current = recognition;
      recognition.start();
    } catch (err) {
      console.error("Recording error:", err);
    }
  };

  const stopRecording = () => {
    recognitionRef.current?.stop();
    pronunciationAnalyzerRef.current?.stopAnalysis();
    currentStreamRef.current?.getTracks().forEach((t) => t.stop());
    setRecording(false);
    clearInterval(recordingTimerRef.current);
    // Auto-advance to next question
    setTimeout(() => {
      setCurrentQuestionIndex((i) => Math.min(i + 1, currentTest.totalQuestions - 1));
    }, 200);
  };

  if (!currentTest) return <div>Loading...</div>;

  const part1Len = currentTest.testStructure.part1.questions.length;
  const part2Index = part1Len;
  const part =
    currentQuestionIndex < part1Len
      ? 1
      : currentQuestionIndex === part2Index
      ? 2
      : 3;
  const currentQuestion = currentTest.allQuestions[currentQuestionIndex];

  return (
    <div className="speech-evaluator">
      {/* Part 2 Preparation Intro */}
      {part === 2 && !isPreparingPart2 && preparationTime === 0 && !recording && (
        <div className="part2-preparation-intro">
          <h3>üìù Part 2: Preparation Phase</h3>
          <button onClick={startPart2Preparation} className="btn-primary">
            Start Preparation (1 minute)
          </button>
        </div>
      )}

      {/* Part 2 Cue Card & Timer */}
      {part === 2 && (isPreparingPart2 || preparationTime === 0) && (
        <div className="question-section">
          <div className="cue-card">
            <h3>{currentQuestion.cueCard.mainPrompt}</h3>
            <ul>
              {currentQuestion.cueCard.bulletPoints.map((pt, idx) => (
                <li key={idx}>{pt}</li>
              ))}
            </ul>
          </div>
          {isPreparingPart2 && (
            <div className="preparation-phase">
              <h4>Time Remaining: {formatTime(preparationTime)}</h4>
            </div>
          )}
        </div>
      )}

      {/* Part 1 & 3 Question */}
      {part !== 2 && (
        <div className="question-section">
          <h3>{currentQuestion.prompt}</h3>
        </div>
      )}

      {/* Recording Controls */}
      {!recording && part !== 2 && (
        <button onClick={startRecording} className="record-button">
          üé§ Start Recording
        </button>
      )}
      {recording && (
        <div className="auto-recording-status">
          <span>‚óè Recording: {formatTime(recordingTime)}</span>
          <button onClick={stopRecording} className="record-button recording">
            ‚èπÔ∏è Stop Recording
          </button>
        </div>
      )}

      {/* Answer Textarea */}
      <textarea
        className="answer-section"
        value={answers[currentQuestionIndex] || ""}
        onChange={(e) =>
          setAnswers((a) => {
            a[currentQuestionIndex] = e.target.value;
            return [...a];
          })
        }
        disabled={recording}
        placeholder="Your response..."
      />

      {/* Navigation (hidden via CSS) */}
      <div className="navigation-section">
        <div className="nav-buttons">
          <button onClick={() => setCurrentQuestionIndex((i) => i - 1)}>‚Üê Previous</button>
          <button onClick={() => setCurrentQuestionIndex((i) => i + 1)}>Next ‚Üí</button>
        </div>
      </div>
    </div>
  );
}
