const sampleAnswers = [
  {
    part: 1,
    question: "Do you live in a house or a flat?",
    sample: "I live in a flat in the city centre, which is quite convenient for commuting to work and socialising with friends."
  },
  {
    part: 2,
    question: "Describe an occasion when you lost your way.",
    sample: "When I visited Delhi for the first time, I took the wrong metro line and ended up in a totally different part of the city. Luckily, I had my phone with me and used a map app to find my way."
  },
  {
    part: 3,
    question: "Are young people better at orienting themselves?",
    sample: "Nowadays, young people seem to be quite adept at navigating new places, especially since they're used to using technology like GPS and smartphones."
  }
  // Add more as needed for each real IELTS question
];

export default sampleAnswers;
